CREATE TABLE BUNDESLAND (	ID BIGINT NOT NULL,	NAME VARCHAR (255) NOT NULL,	CONSTRAINT CC1288606507352 PRIMARY KEY ( ID) ) ;CREATE TABLE WAHLKREIS (	ID BIGINT NOT NULL,	BUNDESLANDID BIGINT NOT NULL,	NAME VARCHAR (255),	CONSTRAINT CC1288606603901 PRIMARY KEY ( ID),	CONSTRAINT CC1288606617285 FOREIGN KEY (BUNDESLANDID)	REFERENCES BUNDESLAND (ID) ON DELETE NO ACTION ON UPDATE NO	ACTION ENFORCED ENABLE QUERY OPTIMIZATION )	ORGANIZE BY DIMENSIONS ( BUNDESLANDID) ;CREATE TABLE WAHLBEZIRK (	ID BIGINT NOT NULL,	WAHLKREISID BIGINT NOT NULL,	CONSTRAINT CC1288606788792 PRIMARY KEY ( ID, WAHLKREISID),	CONSTRAINT CC1288606799462 FOREIGN KEY (WAHLKREISID)	REFERENCES WAHLKREIS (ID) ON DELETE NO ACTION ON UPDATE NO	ACTION ENFORCED ENABLE QUERY OPTIMIZATION )	ORGANIZE BY DIMENSIONS ( WAHLKREISID) ;CREATE TABLE PARTEI (	ID BIGINT NOT NULL,	NAME VARCHAR (255),	KUERZEL VARCHAR (63) NOT NULL,	CONSTRAINT CC1288606983948 PRIMARY KEY ( ID) );CREATE TABLE KANDIDAT (	ID BIGINT NOT NULL GENERATED ALWAYS AS IDENTITY (START WITH	0, INCREMENT BY 1, NO CACHE ),	PARTEIID BIGINT,	BUNDESLANDID BIGINT,DMWAHLKREISID BIGINT,DMPARTEIID	BIGINT,NACHNAME VARCHAR (255) NOT NULL,	VORNAME VARCHAR (255) NOT NULL,	BERUF VARCHAR (255), GEBURTSDATUM DATE,	GEBURTSORT VARCHAR (255),ANSCHRIFT VARCHAR (2047),	LISTENPLATZ INTEGER,CONSTRAINT CC1288607383356 PRIMARY KEY	( ID),	CONSTRAINT CC1288607389830 FOREIGN KEY (PARTEIID) REFERENCES	PARTEI (ID) ON DELETE NO ACTION ON UPDATE NO ACTION ENFORCED	ENABLE QUERY OPTIMIZATION,	CONSTRAINT CC1288607385362 FOREIGN KEY (DMWAHLKREISID)	REFERENCES WAHLKREIS (ID) ON DELETE NO ACTION ON UPDATE NO	ACTION ENFORCED ENABLE QUERY OPTIMIZATION,	CONSTRAINT CC1288607388564 FOREIGN KEY (DMPARTEIID)	REFERENCES PARTEI (ID) ON DELETE NO ACTION ON UPDATE NO	ACTION ENFORCED ENABLE QUERY OPTIMIZATION,	CONSTRAINT CC1288607382330 FOREIGN KEY (BUNDESLANDID)	REFERENCES BUNDESLAND (ID) ON DELETE NO ACTION ON UPDATE NO	ACTION ENFORCED ENABLE QUERY OPTIMIZATION )	ORGANIZE BY DIMENSIONS ( PARTEIID, BUNDESLANDID) ;CREATE TABLE STIMME (	ID BIGINT NOT NULL GENERATED ALWAYS AS IDENTITY (START WITH	0, INCREMENT BY 1, NO CACHE ),	KandidatID BIGINT,	ParteiID BIGINT,	WahlbezirkID BIGINT NOT NULL,	WahlkreisID BIGINT NOT NULL,	Jahr INTEGER,	CONSTRAINT CC1288610679447 PRIMARY KEY ( ID),	CONSTRAINT CC1288610689165 FOREIGN KEY ( KandidatID)	REFERENCES KANDIDAT (ID) ON DELETE NO ACTION ON UPDATE NO	ACTION ENFORCED ENABLE QUERY OPTIMIZATION,	CONSTRAINT CC1288610702435 FOREIGN KEY (PARTEIID) REFERENCES	PARTEI (ID) ON DELETE NO ACTION ON UPDATE NO ACTION ENFORCED	ENABLE QUERY OPTIMIZATION )	ORGANIZE BY DIMENSIONS ( KANDIDATID, WAHLBEZIRKID,	WAHLKREISID) ;CREATE TABLE ERSTSTIMMENNACHWAHLKREIS (	KANDIDATID BIGINT NOT NULL,	WAHLKREISID BIGINT NOT NULL,	JAHR INTEGER NOT NULL,	ANZAHL INTEGER NOT NULL,	CONSTRAINT CC1288610976900 PRIMARY KEY ( KANDIDATID,	WAHLKREISID, JAHR),	CONSTRAINT CC1288610994045 FOREIGN KEY (WAHLKREISID)	REFERENCES WAHLKREIS (ID) ON DELETE NO ACTION ON UPDATE NO	ACTION ENFORCED ENABLE QUERY OPTIMIZATION )	ORGANIZE BY DIMENSIONS ( KANDIDATID, WAHLKREISID) ;COMMENT ON TABLE ERSTSTIMMENNACHWAHLKREIS IS 'Wahlergebnis 1. Stimme';CREATE TABLE ZWEITSTIMMENNACHWAHLKREIS (	PARTEIID BIGINT NOT NULL,	WAHLKREISID BIGINT NOT NULL,JAHR INTEGER NOT NULL,	ANZAHL INTEGER NOT NULL,	CONSTRAINT CC1288610976900 PRIMARY KEY ( PARTEIID,	WAHLKREISID, JAHR),	CONSTRAINT CC1288610983160 FOREIGN KEY (PARTEIID) REFERENCES	PARTEI (ID) ON DELETE NO ACTION ON UPDATE NO ACTION ENFORCED	ENABLE QUERY OPTIMIZATION,	CONSTRAINT CC1288610994045 FOREIGN KEY (WAHLKREISID)	REFERENCES WAHLKREIS (ID) ON DELETE NO ACTION ON UPDATE NO	ACTION ENFORCED ENABLE QUERY OPTIMIZATION )	ORGANIZE BY DIMENSIONS ( PARTEIID, WAHLKREISID) ;COMMENT ON TABLE ZWEITSTIMMENNACHWAHLKREIS IS 'Wahlergebnis 2. Stimme';CREATE TABLE Wahlberechtigter (	ID BIGINT PRIMARY KEY GENERATED ALWAYS AS IDENTITY (START	WITH 0, INCREMENT BY 1, NO CACHE ),	WahlkreisID BIGINT REFERENCES Wahlkreis,	WahlbezirkID BIGINT,	Gewaehlt INTEGER WITH DEFAULT 0);CREATE INDEX WAHLKREISIDINDEX ON KORBI.WAHLBERECHTIGTER(WAHLKREISID ASC) PCTFREE 10 ALLOW REVERSE SCANS PAGE SPLITSYMMETRIC COLLECT SAMPLED DETAILED STATISTICS ;CREATE TABLE WahlkreisDaten (	WahlkreisID BIGINT NOT NULL PRIMARY KEY REFERENCES	Wahlkreis,	AnzahlWahlberechtigte BIGINT,	AnzahlUngueltigeErststimmen BIGINT,	AnzahlUngueltigeZweitstimmen BIGINT,	Jahr BIGINT);CREATE TABLE SessionIDs (	ID VARCHAR (128) PRIMARY KEY NOT NULL,	WahlkreisID BIGINT NOT NULL REFERENCES Wahlkreis,	WahlbezirkID BIGINT NOT NULL);CREATE TABLE ZUFALLSZAHLENDIREKTMANDATE (	ZEILE BIGINT NOT NULL GENERATED ALWAYS AS IDENTITY (START	WITH 0, INCREMENT BY 1, NO CACHE ),	ZAHL BIGINT NOT NULL);CREATE TABLE ZUFALLSZAHLENSitzeNachPartei (	ZEILE BIGINT NOT NULL GENERATED ALWAYS AS IDENTITY (START	WITH 0, INCREMENT BY 1, NO CACHE ),	ZAHL BIGINT NOT NULL);CREATE TABLE ZUFALLSZAHLENSitzeNachLandeslisten (	ZEILE BIGINT NOT NULL GENERATED ALWAYS AS IDENTITY (START	WITH 0, INCREMENT BY 1, NO CACHE ),	ZAHL BIGINT NOT NULL);