ZugriffsreihenfolgeSitzeNachPartei AS (	SELECT p.ParteiID, z.AnzahlStimmen, 		(z.AnzahlStimmen / d.wert) as DivWert,		ROW_NUMBER() OVER (ORDER BY			(z.AnzahlStimmen / d.wert) DESC,			rnd.Zahl DESC) as Rang	FROM ParteienImBundestag p, ZweitStimmenNachPartei z,		Divisoren d, ZufallsZahlenSitzeNachPartei rnd	WHERE p.ParteiID = z.ParteiID 		AND rnd.Zeile = MOD(p.ParteiID + (d.wert / 1)*			(SELECT COUNT(*) FROM Partei), 			(SELECT COUNT(*)			 FROM ZufallsZahlenSitzeNachPartei))), 