ZugriffsreihenfolgeSitzeNachLandeslisten AS (	SELECT p.ParteiID, z.BundeslandID, z.AnzahlStimmen,		(z.AnzahlStimmen / d.wert) as DivWert,		ROW_NUMBER() OVER (PARTITION BY p.ParteiID 			ORDER BY (z.AnzahlStimmen / d.wert) DESC,			rnd.Zahl DESC) as Rang	FROM ParteienImBundestag p, ZweitStimmenNachBundesland z,		Divisoren d,		ZufallsZahlenSitzeNachLandeslisten rnd	WHERE p.ParteiID = z.ParteiID 		AND rnd.Zeile = MOD(p.ParteiID + 			(SELECT COUNT(*) FROM Partei) *			(z.BundeslandID + 			(SELECT COUNT(*) FROM Bundesland) * 			(d.wert / 1)),(SELECT COUNT(*) FROM			ZufallsZahlenSitzeNachLandeslisten))), 